//context.testCase.testSuite.project.setPropertyValue("tenant_ID", json.tenants[0].id)

for(def tenant in json.tenants) {
	if(tenant.master_tenant == true) {
		context.testCase.testSuite.project.setPropertyValue("tenant_id", tenant.id)
		break
	}
}

