import groovy.json.JsonSlurper
def response = messageExchange.response.responseContent
assert response != ""
def jsonslurper = new JsonSlurper().parseText(response)
assert jsonslurper.token.access_token != ""
assert jsonslurper.tenants!= ""
context.testCase.testSuite.setPropertyValue("x_auth_token", jsonslurper.token.access_token)
//context.testCase.testSuite.setPropertyValue("tenant_id", jsonslurper.tenants[56].id)

if(jsonslurper.tenants != {})
{
	for(def i = 0 ; i < jsonslurper.tenants.size() ; i ++)
	{
		if(jsonslurper.tenants[i].name == "QA_MSP")
		{
			context.testCase.testSuite.setPropertyValue("tenant_id", jsonslurper.tenants[i].id)
			log.info jsonslurper.tenants[i].id
			break
		}
	}
}

