import groovy.json.JsonSlurper
def json_1 = testRunner.testCase.getTestStepByName("activity-cofirmation-status").getPropertyValue("response")
def response1 = new JsonSlurper().parseText(json_1)



def flag = false
if (response1.data.status == "completed") 
{
  flag = true
  return "Activity configured successfully" 
}
else
{
    testRunner.fail "Activity configuration failed"
}

log.info

import groovy.json.JsonSlurper
def json_1 = testRunner.testCase.getTestStepByName("Activity_Page").getPropertyValue("response")
def jsonslurper = new JsonSlurper().parseText(json_1)

def aws_account_id = context.testCase.testSuite.getPropertyValue("account_id_aws")
log.info aws_account_id
def flag = false
if(jsonslurper.data.cloud_account_configuration != [])
{ 

	for(def i = 0 ; i < jsonslurper.data.cloud_account_configuration.size() ; i ++)
	{ 
		if(jsonslurper.data.cloud_account_configuration[i].cloud_account_id == aws_account_id && jsonslurper.data.cloud_account_configuration[i].status == "completed")
		{	
			flag = true
			return "Template has been applied"
		}

	}
}
else
{
	testRunner.fail "No data fetched for cloud accounts."
}
if(flag == false)
{
	testRunner.fail "Template could not be applied"
}

