import groovy.json.JsonSlurper
def json_1 = testRunner.testCase.getTestStepByName("Confirm-Created-Configuration").getPropertyValue("response")
def jsonslurper = new JsonSlurper().parseText(json_1)

def flag = false
if(jsonslurper.data.configuration_details != [])
{   
	for(def i = 0 ; i < jsonslurper.data.configuration_details.size() ; i ++)
	{
		if(jsonslurper.data.configuration_details[0].status == "completed")
		{		
			flag = true
			return "Alert configured"
		}

	}
}
else
{
	testRunner.fail "No data fetched for cloud account"
}
if(flag == false)
{
	testRunner.fail "Alert not configured"
}

