import groovy.json.JsonSlurper
def response = messageExchange.response.responseContent
assert response != ""
def jsonslurper = new JsonSlurper().parseText(response)
//assert jsonslurper.tenants != ""
assert jsonslurper.token.access_token != ""
context.testCase.testSuite.project.setPropertyValue("x_auth_token", jsonslurper.token.access_token)
context.testCase.testSuite.setPropertyValue("x_auth_token", jsonslurper.token.access_token)
//context.testCase.testSuite.setPropertyValue("tenant_id", jsonslurper.tenants[0].id)


def TENANT_ID = "64d0e27e0405a9f4fb37d55f"
def flag = false
ArrayList<String> tenant_ids = new ArrayList<String>()

assert jsonslurper.tenants != []
for(def tenant in jsonslurper.tenants)
{
	tenant_ids.add(tenant.id)
}
assert tenant_ids.contains(TENANT_ID)
context.testCase.testSuite.setPropertyValue("tenant_id", TENANT_ID)
context.testCase.testSuite.project.setPropertyValue("tenant_id", TENANT_ID)
//log.info tenant_ids

