import groovy.json.JsonSlurper
def json_1 = testRunner.testCase.getTestStepByName("pre-execute _ template").getPropertyValue("response")
def jsonslurper = new JsonSlurper().parseText(json_1)
if(jsonslurper.data.service_accounts !=[])
{
	for(def i = 0 ; i < jsonslurper.data.service_accounts.size() ; i ++)
	{
		if(jsonslurper.data.service_accounts[0].id == "64d1e2f91d985e3190b0fe1a")
		{
			context.testCase.testSuite.setPropertyValue("Azure_account_location", jsonslurper.data.service_accounts[i].metadata.locations[0].id)
			context.testCase.testSuite.setPropertyValue("Azure_account_resourcegroup", jsonslurper.data.service_accounts[i].metadata.resourcegroup[0].id)
			context.testCase.testSuite.setPropertyValue("Azure_account_name", jsonslurper.data.service_accounts[i].name)
			context.testCase.testSuite.setPropertyValue("service_name", jsonslurper.data.service_accounts[i].service_name)
			break
		}
	}
}
else
{
	testRunner.fail "Cloud Account not found"
}

