if(jsonslurper.data.visibility_data != [])
{   
//	log.info "not matched"
	for(def i = 0 ; i < jsonslurper.data.visibility_data.size() ; i ++)
	{
		//log.info "not matched"
		if(jsonslurper.data.visibility_data[i].service_account_name == "CS_SaaS_QA_INFRA_120723-expiry")
		{		
			if(jsonslurper.data.visibility_data[i].resource_name == resource_name)
			{
				flag2 = true
				return "Resource name got matched" 
			}
			flag1 = true
//			return "Resource has been created"
		}

	}
}
else
{
	testRunner.fail "No data fetched for the resource"
}
if(flag2 == false)
{
	testRunner.fail "Resource name did not get matched"
}

