// Get the start time of the API call
def startTime = System.currentTimeMillis()

// Execute the API call
// Replace 'YourAPICall' with the actual name of your API call in SoapUI
//testRunner.runTestStepByName("Configure-Alerts-AWS")


def configureAlertsAWSApi = context.testCase.testSteps["Configure-Alerts-AWS"]
def response = configureAlertsAWSApi.run(testRunner, context)
testRunner.cancel("configureAlertsAWSApi")

// Get the end time of the API call
def endTime = System.currentTimeMillis()

// Calculate the execution time in seconds
def executionTime = (endTime - startTime) / 1000

log.info("API Execution Time: ${executionTime} seconds")

// Check if the execution time exceeds 10 seconds
if (executionTime > 10) {
    log.error("API execution time exceeds 10 seconds. Failing the test.")
    assert false, "API execution time exceeds 10 seconds."
} else {
    log.info("API execution within the acceptable time limit.")
}

