import com.aventstack.extentreports.*;
import groovy.json.JsonSlurper
import groovy.json.JsonOutput;
import groovy.json.*
import com.aventstack.extentreports.markuputils.MarkupHelper
import java.util.Arrays;

def jsonSlurper = new JsonSlurper()


// Get the project path
def now = new Date()
def CurrentDate = now.format("yyyy-MM-dd_hh-mm-ss", TimeZone.getTimeZone('Asia/Kolkata'))
def projectPath = new com.eviware.soapui.support.GroovyUtils(context).projectPath
def relativeFilePath = projectPath//replace("\\","//");
def actualProjectPath = relativeFilePath + "\\Reports\\soapuiExecutionReport_SmokeTesting"  + ".html"


ExtentHtmlReporter htmlReporter = new ExtentHtmlReporter(actualProjectPath);
ExtentReports extent = new ExtentReports();
ExecutionFrom = com.eviware.soapui.SoapUI.globalProperties.getPropertyValue("ExecutionFrom")

extent.attachReporter(htmlReporter);
extent.setSystemInfo("Environment", project.getPropertyValue("Environment"));
extent.setSystemInfo("Release", project.getPropertyValue("QA-Release"));
extent.setSystemInfo("EndPoint", project.getPropertyValue("EndPoint"));
extent.setSystemInfo("EndPoint_Inventory", project.getPropertyValue("EndPoint_Inventory"));
extent.setSystemInfo("TestingTool", "SoapUI 5.6.0");
extent.setSystemInfo("Executer", "Gayathri");

htmlReporter.config().setDocumentTitle("Corestack API Automation Testing Report");
htmlReporter.config().setReportName("<a href=https://www.corestack.io>" + "<img src=https://cdn.shortpixel.ai/spai/ret_img/https://www.corestack.io/wp-content/uploads/CoreStack-300X90.svg width=130 height=48></a> <b> API Automation Testing Report</b>");
htmlReporter.config().setChartVisibilityOnOpen(true);

// Actual logic for Reports update
ArrayList < String > failedMessages = new ArrayList < String > ();


try
{
    runner.getResults().each
    {
        testSuiteRunner -> ExtentTest parentTest = extent.createTest(testSuiteRunner.getTestSuite().getName());
        try
        {
            testSuiteRunner.getResults().each
            {
                testCaseRunner -> ExtentTest test = parentTest.createNode(testCaseRunner.getTestCase().getName(), testCaseRunner.getTestCase().getDescription());
                def authorName = 'Gayathri'
                test.assignAuthor(authorName)
                def categoryName = ""
                // if()
                // def categoryName = testSuiteRunner.getTestSuite().getName()
                // log.info categoryName
                // def categoryName = testSuiteRunner.getTestSuite().getPropertyValue("category")
                // log.info "CATEGORU"
                if(testSuiteRunner.getTestSuite().getPropertyValue("category") == null)
                {
                    categoryName = testSuiteRunner.getTestSuite().getName()
                }
                else
                {
                    categoryName = testSuiteRunner.getTestSuite().getPropertyValue("category")
                }
                // log.info testSuiteRunner.getTestSuite().getPropertyValue("category")
                // log.info testSuiteRunner.getTestSuite().getPropertyValue("category").getClass()
                test.assignCategory(categoryName)
                try
                {
                    testCaseRunner.getResults().each
                    {
                        testStepResult ->
                        // log.info('Test Step ' + testStepResult.getTestStep().getName() + ' TestRunner.status = ' + testStepResult.getStatus());
                        def testSuiteName = testSuiteRunner.getTestSuite().getName()
                        def testCaseName = testCaseRunner.getTestCase().getName()
                        def testStepName = testStepResult.getTestStep().getName()
                        def Request = "NULL"
                        def Response = "NULL"
                        def GroovyScript = "NULL"
                        def Req = "NULL"
                        def Res = "NULL"
                        def actualData
                        APIdetails = ""
                        APIMethod = "Step to create test data or transfer data"
                        SLA = "0"

                        def testStepStatus = testStepResult.getStatus();
                        if(testStepStatus.toString() == "OK")
                        {
                            if(testStepResult.getTestStep().config.type == "restrequest")
                            {
                                if (testStepResult.response != null) 
                                {
                                    SLA = testStepResult.response.timeTaken
                                    // log.info "SLA : " + SLA
                                    def name = testStepResult.getTestStep().getName()
                                    def time_taken = testStepResult.getTimeTaken()
                                    def status = testStepResult.getStatus()
                                    def resource1 = testStepResult.getRequestContent()
                                    def resource = JsonOutput.prettyPrint(resource1)
                                    def output = testStepResult.getResponseContent()
                                    // log.info output
                                    def final_output = ""
                                    try
                                    {
                                        final_output = JsonOutput.prettyPrint(output)
                                    }
                                    catch(Exception e)
                                    {
                                        final_output = output
                                    }
                                    def response_headers = testStepResult.getResponseHeaders()
                                    def url = testStepResult.getProperty("URL")

                                    def method = testStepResult.getProperty("Method")
                                    def uri = testStepResult.getTestStep().getResource()
                                    if(SLA < 5000)
                                    {
                                        response = name + "\n\n" + "Status : " + "" + status + "\n\n" + "API details : \n" + "URL : " + url + "\n"+ "URI details : " + uri + "\n" + "Method Type : " + method + "\n" + "Response Time : " +  time_taken + " ms" + "\n" + "Response Headers : " + response_headers["#status#"] + "\n\n" + "Request Paylod : \n " + resource + "\n\n" +"Response : \n" + final_output
                                        test.pass(MarkupHelper.createCodeBlock(response))
                                    }
                                    else
                                    {
                                        response = name + "\n\n" + "Status : Response Time Warning." + "" + "\n\n" + "API details : \n" + "URL : " + url + "\n" + "Method Type : " + method + "\n" +  "Response Time : " +  time_taken + " ms" + "\n" + "Response Headers : " + response_headers["#status#"] + "\n\n" + "Request Paylod : \n " + resource + "\n\n" +"Response : \n" + final_output
                                        test.warning(MarkupHelper.createCodeBlock(response))
                                    }
                                }
                                else
                                {
                                    test.fail("No SLA found for the method")
                                }
                                // com.eviware.soapui.SoapUI.logMonitor.getLogArea( "script log" ).clear()
                            }
                            else if(testStepResult.getTestStep().config.type == "groovy")
                            {
                                def status = testStepResult.getStatus()
                                def time_taken = testStepResult.getTimeTaken()
                                script_text = "___________________________________________________________________________________________________________________________" + "\n" + testStepResult.getTestStep().getScript() + "\n" + "___________________________________________________________________________________________________________________________" 
                                // response = testStepName + "\n\n" + "Status : " + "" + status +  "\n\n" + "Response Time : " + time_taken + " ms" + "\n"+ "Script details : " + "\n" + script_text + "\n" + "Messages : " + testStepResult.getProperties()["messages"] + "\n\n" 
                                response = testStepName + "\n\n" + "Status : " + "" + status +  "\n\n" + "Response Time : " + time_taken + " ms" + "\n" + "\n" + "Messages : " + testStepResult.getProperties()["messages"] + "\n\n"
                                groovy_logs = ""
                                // com.eviware.soapui.SoapUI.logMonitor.getLogArea( "script log" ).clear()
                                if(SLA < 1000)
                                {

                                    test.pass(MarkupHelper.createCodeBlock(response))

                                }
                                else
                                {
                                    test.warning(MarkupHelper.createCodeBlock(response))
                                }
                            }
                            else
                            {
                                response = response = testStepResult.getTestStep().getName() + "\n\n" + "Status : " + testStepStatus.toString() + "\n\n" + "ResponseTime:" + SLA + "ms"
                                if(SLA < 1000)
                                {
                                    test.pass(MarkupHelper.createCodeBlock(response))

                                }
                                else
                                {
                                    test.warning(MarkupHelper.createCodeBlock(response))
                                }

                            }
                        }
                        else
                        {
                            if(testStepResult.getTestStep().config.type == "groovy")
                            {
                                script_text = "___________________________________________________________________________________________________________________________" + "\n" + testStepResult.getTestStep().getScript() + "\n" + "___________________________________________________________________________________________________________________________" 
                                script_logs = testCaseRunner.getReason()
                                def failure_reason = testCaseRunner.getReason()
                                // errors = testStepResult.getTestStep().getErrors()
                                // log.info "ERRORS : " + testStepResult.getProperties()["messages"]
                                // response = testStepName + "\n\n" + "Status : " + testStepStatus.toString()  + "\n\n" + "Response Time : " + testStepResult.getTimeTaken() + " ms" + "\n" + "Script details : " + "\n\n" + script_text+ "\n\n" +   "Errors : " + testStepResult.getProperties()["messages"] + "\n\n" +  "failure reason : " + testCaseRunner.getReason()

                                response = testStepName + "\n\n" + "Status : " + testStepStatus.toString()  + "\n\n" + "Response Time : " + testStepResult.getTimeTaken() + " ms" + "\n" + "\n" +   "Errors : " + testStepResult.getProperties()["messages"] + "\n\n" +  "failure reason : " + testCaseRunner.getReason()
                                // test.fail(testStepName + "<h6 style=color:DarkMagenta>" + "</h6>" + " <b style=color:red>Groovy script or property transfer failure. <b> " + "<span style='font-weight:bold;'></spa , n>" + "\n\n")
                                failedMessages.clear();
                                test.fail(MarkupHelper.createCodeBlock(response))
                            }
                            else if(testStepResult.getTestStep().config.type == "restrequest")
                            {
                                testStepResult.getMessages().each
                                {
                                    message -> failedMessages.add(message);
                                }
                                def name = testStepResult.getTestStep().getName()
                                def time_taken = testStepResult.getTimeTaken()
                                def status = testStepResult.getStatus()
                                def resource1 = testStepResult.getRequestContent()
                                def resource = JsonOutput.prettyPrint(resource1)
                                def output = testStepResult.getResponseContent()
                                try
                                {
                                    final_output = JsonOutput.prettyPrint(output)
                                }
                                catch(Exception e)
                                {
                                    final_output = output
                                }
                                // final_output = JsonOutput.prettyPrint(output)
                                def response_headers = testStepResult.getResponseHeaders()
                                def failure_messages = ""
                                def url = testStepResult.getProperty("URL")
                                def method = testStepResult.getProperty("Method")
                                // log.info "///////////////////////////////////////////////////////////////"
                                // log.info testStepResult.getProperties()
                                def uri = testStepResult.getTestStep().getResource()
                                // log.info "RESOURCE : " + uri
                                if(failedMessages.size() > 0)
                                {
                                    for(int i = 0 ; i < failedMessages.size() ; i ++)
                                    {
                                        failure_messages += failedMessages[i] + "\n"
                                    }
                                }
                                // log.info failure_messages
                                response = name + "\n\n" + "Status : FAILED." +  "\n\n" + "Failures : \n" + failure_messages + "\n\n" + "API details : \n" + "URL : " + url + "\n" + "URI details : " + uri + "\n" + "Method Type : " + method + "\n" + "Response Time : " +  time_taken + " ms" + "\n" + "Response Headers : " + response_headers["#status#"] + "\n\n" + "Request Paylod : \n " + resource + "\n\n" +"Response : \n" + final_output
                                // test.fail(testStepName + "<h6 style=color:DarkMagenta>" + APIMethod + " " + APIdetails + "<h6 style=color:blue>ResponseTime:" + SLA + "ms</h6>" + " <b style=color:red>Failed due to script assertion. Check Details | <b> " + "<span style='font-weight:bold;'></span>" + failedMessages);
                                failedMessages.clear();
                                // failure_messages = ""
                                test.fail(MarkupHelper.createCodeBlock(response))

                            }
                            else
                            {
                                def status = testStepResult.getStatus()
                                def errors = testStepResult.getError()
                                // log.info testStepResult.getProperties()["messages"].getProperties()
                                // log.info testStepResult.getProperties()
                                testStepResult.getMessages().each
                                {
                                    message -> failedMessages.add(message);
                                }
                                // log.info "failed message : " + failedMessages
                                // test.fail(testStepName +   "<h6 style=color:DarkMagenta>" + "</h6>" + " <b style=color:red>Groovy script or property transfer failure. <b> " + "<span style='font-weight:bold;'></spa , n>" + "\n\n")
                                response = testStepResult.testStep.getName() + "\n\n" + "Status : " + status + "\n\n" + "Errors : " + "\n" + failedMessages              
                                // log.info testStepResult.getError()
                                test.fail(MarkupHelper.createCodeBlock(response))
                                failedMessages.clear();
                            }
                            // com.eviware.soapui.SoapUI.logMonitor.getLogArea( "script log" ).clear()
                        }
                    }


                }
                catch(Exception e)
                {
                    log.info "Exception at Testcase level : " + e
                }

            }
        }
        catch(Exception e)
        {
            log.info "Exception at Testsuite level : " + e
        }
    }
}
catch(Exception e)
{
    log.info "Exception at Runner level : " + e
}

